      border: dashed 2px black;
      margin-bottom: 2vh;
      margin-top: 5vh;
      text-align: center;

      p {
        margin: 5px 0;

        span {
          font-weight: bold;
        }
      }
    }

    .fieldsets {
      display: -webkit-flex;
      display: -moz-flex;
      display: -ms-flexbox;
      display: -o-flex;
      display: flex;
      -webkit-box-orient: vertical;
      -webkit-box-direction: normal;
      -ms-flex-direction: column;
      flex-direction: column;

      fieldset {
        width: 100%;
        height: 100%;
        padding: 20px;
        text-align: center;
        border: solid 2px black;
        margin: 20px 0;

        legend {
          width: 100%;
          font-family: catamaran, arial, sans-serif;
          font-size: 2em;
          font-weight: bold;
          text-align: center;
          letter-spacing: 2px;
        }

        div.options {
          display: inline;
          width: 100%;

          @supports(flex-wrap: wrap) {
            display: -webkit-flex;
            display: -moz-flex;
            display: -ms-flexbox;
            display: -o-flex;
            display: flex;
            -webkit-box-orient: horizontal;
            -webkit-box-direction: normal;
            -ms-flex-direction: row;
            flex-direction: row;
            -ms-flex-pack: distribute;
            justify-content: space-around;
            -webkit-box-align: center;
            -ms-flex-align: center;
            align-items: center;
            -ms-flex-wrap: wrap;
            flex-wrap: wrap;
          }

          input[type="radio"] {
            position: absolute;
          }

          input[type="radio"] + label {
            padding: 20px;
            border: solid 2px rgba(0,0,0,0);
            transition: all .3s;
          }

          input[type="radio"]:hover + label {
            cursor: pointer;
          }

          label {
            width: 100%;
            padding: 15px;
            font-family: quicksand, arial, sans-serif;
            text-transform: uppercase;
            letter-spacing: 1px;

            img {
              margin: 0 auto 10px;
              max-width: 80px;
              display: block;
            }
          }
          div {
            padding: 5px;
            margin: 0;

            input[type="radio"]:checked + label {
              border: solid 2px $semi-black;
              transition: all .3s;
            }
          }
        }

        div.options.color {
          input[type="radio"]#red:checked + label {
            border: solid 2px $semi-black;
            background-color: $red;
            color: $white;
          }

          input[type="radio"]#green:checked + label {
            border: solid 2px $semi-black;
            background-color: $green;
            color: $black;
          }

          input[type="radio"]#white:checked + label {
            border: solid 2px $semi-black;
            background-color: $white;
            color: $black;
          }

          input[type="radio"]#black:checked + label {
            border: solid 2px $semi-black;
            background-color: $semi-black;
            color: $white;
          }

          input[type="radio"]#blue:checked + label {
            border: solid 2px $semi-black;
            background-color: $blue;
            color: $black;
          }

           input[type="radio"]#pink:checked + label {
            border: solid 2px $semi-black;
            background-color: $pink;
            color: $black;
          }
        }
      }
    }

    input[type="submit"] {
      position: relative;
      top: 10px;
      border: solid 2px $semi-black;
      width: 100%;
      padding: 10px 0;
      background: $semi-black;
      color: $white;
      font-family: catamaran, arial, sans-serif;
      font-weight: bold;
      font-size: 1em;
      transition: all .3s;
    }

    input[type="submit"]:hover {
      background-color: rgba(0,0,0,0);
      color: $semi-black;
      transition: all .3s;
    }
  }
}


main div.carousel-active {
  align-items: center;

  section.t-shirt {
    .text-display {
      display: block;
      position: absolute;
      left: 50%;
      top: 30%;
      transform: translate(-50%, -50%);
      max-width: 40%;
      text-align: center;
      z-index: 999;
    }
  }

  section.choose {
    .unique-code {
      margin-top: 0;
    }

    div.fieldsets {
      position: relative;
      display: block;
      min-height: 580px;
      overflow: hidden;
      border: solid 2px $semi-black;

      @supports(flex-wrap: wrap) {
        min-height: 350px;
      }

      fieldset {
        position: absolute;
        left: 0;
        top: 20px;
        margin: 0;
        box-sizing: border-box;
        border: none;
        transition: all .5s .3s ease-in-out;

        &:nth-of-type(2) {
          left: 100%;
        }

        &:nth-of-type(3) {
          left: 200%;
        }

        &:nth-of-type(4) {
          left: 300%;
        }

         &:nth-of-type(5) {
          left: 400%;
        }
      }

      input[type="submit"] {
        top: 30px;
      }
    }
  }
}

